import React, { Component } from "react";
import Hero from "./components/Hero";
import heroImage from "./assets/llamacorn_residents.JPG";
import Card from "./components/Card";
import API from "./utils/API";
import Sort from "./components/Filter";

class App extends Component {
    state = {
        result: {},
        search: ""
    };

    componentDidMount() {
        this.searchResident();
    }


    searchResident = (id) => {
        let residents = []
        for (id = 10; id < 160; id += 10) {
            API.search(id)
                .then(res => {
                    residents.push(res.data)
                    this.setState({ result: residents })
                }
                )
                .catch(err => console.log(err));
        }
    }

    render() {

        let response = this.state.result;
        console.log(response)

        if (!response) {
            return
        }

        return (
            <div className="all-container">
                <Hero backgroundImage={heroImage} />
                <Nav/>
                <Card residents={this.state.result} />
            </div>
        )
    }
}

export default App;
